import React,{useContext,useState} from 'react'
import './PostTable.css'
import {GetImgPreview} from '../user/GetImgPreview';
import axios from 'axios';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


function PostTable(probs) {
    console.table(probs.products)
    const products = probs.products;
    const {model,setmodel,tempImg,settempImg} = useContext(GetImgPreview);

    function setimg(img){
        settempImg(img);
        setmodel(true);
    }
    function GetLikeCount(id){
        
        const [count, setcount] = useState("")
        axios.get(`http://localhost:8989/api/like/get_like_count/${id}`)
        .then((res)=>{
            console.log(res.data.count);
            setcount(res.data.count)  
            
        } )
        .catch(error => console.log(error));
        return(count);
    }
    function deletePost(id,src,path){
        notify(id,src,path)
    }

    const notify = (id,src,path) => {
        toast.error(yesno(id,src,path),{
            position: "top-center",
            autoClose: false,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
        
        })
    };
    const yesno =(id,src,path)=>{
        console.log({Src:src})
        function clickYes(){
            
            
            axios.delete(`http://localhost:8989/product/delete`,{id:id,path:path})
            .then((data)=>{
                notify2("Delete successfuly.")
            }).catch((err)=>{
                console.log(err.response)
            })

        }
        const notify2 = (messege) => {
            toast.success(`${messege}`,{
                position: "top-right",
                autoClose: true,
                hideProgressBar: false,
                closeOnClick: false,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            
            })
        };
        return(
            <div>
                <ul className='yesno-ul'>
                    <li>
                        <img className='yesno-img' src={src}/>
                    </li>
                    <li>
                        <p>Are you sure to delete this?</p>
                    </li>
                </ul>
                <ul className='yesno-ul '>
                    <li>
                        <button type="button" class="btn btn-success">No</button>
                    </li>
                    <li>
                        <button type="button" class="btn btn-danger" onClick={()=>{
                            clickYes(id) 
                        }}>Yes</button>
                    </li>
                </ul>
                

            </div>
        )
    }
    return (
        <div>
            <table class="table table-success table-striped cu-table">
                    <thead>
                        <tr>
                        <th scope="col">#</th>
                        <th scope="col">image</th>
                        <th scope="col">Title</th>
                        <th scope="col">views</th>
                        <th scope="col">Likes</th>
                        <th scope="col">Category</th>
                        <th scope="col">Size</th>
                        <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            products.map((product,index)=>(

                                <tr>
                                    <th scope="row">{index+1}</th>
                                        <td>
                                            {
                                                <img className='table-img' src={`http://localhost:8989/api/${product.productImage}`} onClick={()=>{
                                                    setimg(`http://localhost:8989/api/${product.productImage}`)
                                                }}/>

                                            }
                                        </td>
                                        <td className='td-title'>
                                            <p>{product.title}</p>
                                        </td>
                                        <td>
                                            <p>{product.likes}</p>
                                            
                                        </td>
                                        <td>
                                            <p>{GetLikeCount(product._id)}</p>
                                            
                                        </td>
                                        <td>
                                            <p>{product.category}</p>
                                            
                                        </td>
                                        <td>
                                            <p>{product.size}</p>
                                            
                                        </td>
                                        <td>
                                            {
                                                <div className='table-icon-div' >
                                                    <i class="fas fa-edit" type='update'></i>
                                                    <i class="fas fa-trash-alt" type='delete' alt="Delete" onClick={()=>{
                                                        deletePost(product._id,`http://localhost:8989/api/${product.productImage}`,product.productImage)
                                                    }}></i>

                                                </div>

                                            }
                                            
                                        </td>
                                </tr>
                            ))
                        }
                        
                    </tbody>
                </table>
            
        </div>
    )
}

export default PostTable
