import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom';
import { Button } from './Button';
import './Navbar.css';

function Navbar() {
    const [click,setClick] = useState(false);
    const handleClick = () => setClick(!click);
    const [button,setButton] = useState(true);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <=960){
            setButton(false);

        }else{
            setButton(true);
        }
    };
    useEffect(()=>{
        showButton();
    },[]);

    window.addEventListener('resize',showButton);
    return(
        <>
            <nav className='navbarN'>
                <div className='navbar-containerN'>
                    <Link to="/" className="navbar-logoN" onClick={closeMobileMenu}>
                        HansalakaFineArt <i class="fas fa-paint-brushN"></i> 
                    </Link>
                    <div className='menu-iconN' onClick={handleClick}>
                        <i className={click ? 'fasN fa-timesN' : 'fas fa-barsN'}/>
                    </div>
                    <ul className={click ? 'nav-menu activeN': 'nav-menuN'}>
                        <li>
                            <Link to='/' className='nav-linksN' onClick={closeMobileMenu}>
                                Home
                            </Link>
                        </li>
                        
                        <li>
                            <Link to='/' className='nav-linksN' onClick={closeMobileMenu}>
                                Drowing
                            </Link>
                        </li>
                        <li>
                            <Link to='/' className='nav-linksN' onClick={closeMobileMenu}>
                                About
                            </Link>
                        </li>
                        <li>
                            <Link to='/sign-up' className='nav-links-mobileN' onClick={closeMobileMenu}>
                                Sign Up
                            </Link>
                        </li>
                         
                    </ul>
                    {button && <Button buttonStyle='btn--outlineN'>SIGN UP</Button>}
                </div> 
            </nav>
        </>
    );
} 
export default Navbar;
