    //     const size = Width +" x "+Length+" "+SI;
        
    //     const newPost = {
    //         title,
    //         description,
    //         ailability,
    //         category,
    //         drowingType,
    //         size:size,
            

    //     }
    //     console.log(newPost);
    //     axios.post("http://localhost:8989/product/add",newPost)
    //     .then((res)=>{
    //         console.log(res);    
    //     }).catch((err)=>{
    //         alert("sorry we can't add Event! "+err)
    //     });
    // }
    
    return (
        <div className='ap-b-div'>
            <h1>pasan</h1>
            <div className='form-div'>
            {/* <img src={path} alt='image' className='privewe-img'/> */}
                <form className='ap-from'  >
                    
                    <ul className='ap-form-ul'>
                        <li className='ap-form-li' >
                                <div class="ap-control">
                                
                                <input type="file" class="form-control" id="inputGroupFile03" aria-describedby="inputGroupFileAddon03" aria-label="Upload" onChange={uploadFile}/>
                                
                            </div>
                            <div className='ap-control'>
                                <lable for='name'>Title</lable>
                                <input type='title' class="form-control" id='name' onChange={(e)=>{
                                    setTitle(e.target.value);
                                }}/>
                            </div>
                            <div className='ap-control'>
                                <lable for='text-area'>Description</lable>
                                <textarea  type='text-area' class="form-control" id='text-area' onChange={(e)=>{
                                    setdescription(e.target.value);
                                }}/>
                            </div>
                            <div className=''>
                                <lable className='form-check-label' for='checkbox' onChange={(e)=>{
                                    
                                    setavAilability(e.target.value);
                                }}>availability</lable>
                                <input type='checkbox' id='checkbox'/>
                                
                            </div>
                           
                            <div class="input-group ">
                                <div class="input-group-prepend">
                                    <span class="input-group-text" id="">size (width x length)</span>
                                </div>
                                <input type="text" class="form-control" onChange={(e)=>{
                                    setWidth(e.target.value);
                                }}/>
                                <input type="text" class="form-control" onChange={(e)=>{
                                    setLength(e.target.value);
                                }}/>
                                <select class="custom-select mr-sm-2" id="inlineFormCustomSelect" onChange={(e)=>{
                                    setSI(e.target.value);
                                }}>
                            
                                        <option selected value="mm">mm</option>
                                        <option value="cm">cm</option>
                                        <option value="m">m</option>
                                        <option value="inc">inc</option>
                                    </select>
                            </div>
                        </li>
                        <li className='ap-form-li'>
                            <div className='ap-control'>
                                    <div class="col-auto my-1">
                                        <label class="mr-sm-2" for="inlineFormCustomSelect">Category</label>
                                        <select class="custom-select mr-sm-2" id="inlineFormCustomSelect" onChange={(e)=>{
                                            setCategory(e.target.value);
                                        }}>
                                            <option selected>Choose...</option>
                                            <option value="one">One</option>
                                            <option value="two">Two</option>
                                            <option value="3">Three</option>
                                        </select>
                                        </div>
                            </div>
                            <div className='ap-control'>
                                    <div class="col-auto my-1">
                                        <label class="mr-sm-2" for="inlineFormCustomSelect">Drowing Type</label>
                                        <select class="custom-select mr-sm-2" id="inlineFormCustomSelect" onChange={(e)=>{
                                            setDrowingType(e.target.value);
                                        }}>
                                            <option selected>Choose...</option>
                                            <option value="1">One</option>
                                            <option value="2">Two</option>
                                            <option value="3">Three</option>
                                        </select>
                                        </div>
                            </div>

                        </li>
                        
                    </ul>
                    <button  type="submit" className='s-btn'>Submit</button>
                    
                </form>

            </div>
        </div>
    )
}

export default AddPost
