import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Cards from '../Cards'
import MainSection from '../MainSection'
import './home.css'
import CardItem from '../CardItem';


function Home() {

    const[products, setProducts] =useState([]);
    const[content, setContent] = useState([]);
    const[csskey, setcsskey] = useState("");
    useEffect(() => {
        axios.get(`http://localhost:8989/product/get/${5}`)
        .then(res => setProducts(res.data.modelData))
        .catch(error => console.log(error));
    },[]);
    console.log(products);
    const windowsize = window.innerHeight;
    window.addEventListener('scroll',()=>{
        let content = document.querySelector('.top-drow-div');
        let contentPosition = content.getBoundingClientRect().top;
        let screenPosition = window.innerHeight;
        if(contentPosition < screenPosition){
            content.classList.add('top-drow-div-active')
        }else{
            content.classList.add('top-drow-div-active') 
        }
        
    },[]);
    console.log(content);
    console.log(window.innerHeight-content);
    return (
        <div>
            
            <MainSection/>
            <div className='sec-two-div'>
                {products.map((oneP,index)=>{
                    return(
                        <div className='top-drow-div'  id='div-1'>
                            <CardItem 
                            src={`http://localhost:8989/api/${oneP.productImage}`} 
                            text={`${oneP.description}`}
                            label='Adventure' 
                            path='/servise' 
                            title={`${oneP.title}`} 
                            likes={`${oneP.likes}`}
                            id = {`${oneP._id}`}
                        /> 
                        </div>
                        
                    )
                    })}
                
            </div> 
            
            
            
                
        </div>
        
    )
}

export default Home

