    }
    return(
        
        <div className="dropdown">
        <button className="dropbtn">Hi {name[0]}</button>
            <div className="dropdown-content">
            <Link to={"/"}><a href="# ">Watch list</a></Link>
            <Link to={"/"}><a href="# ">My Profile</a></Link>
            <Link to={"/"}><a href="# " onClick={()=>{
                logoutOnClick()
            }}>Log out</a></Link>
            </div>
      </div>
        
    )
}
