import axios from 'axios';
import React, { useState } from 'react'
import "./AddBus.css";

function UpdateConductor() {
    const [getData, setgetData] = useState([])
    const [conductorName, setconductorName] = useState('')
    const [conductorNIC, setconductorNIC] = useState('')
    const [conductorId, setconductorId] = useState('')
     const [contactNumber,setcontactNumber] = useState('')

   const { id } = useParams();


     useEffect(() => {
    axios.get(`http://localhost:8989/api/conductor/get_one_conductor/${id}`)
    .then((res)=>{
        setgetData(res.data)
        setconductorName(res.data.conductorName)
    setconductorNIC(res.data.conductorNIC)
    setconductorId(res.data.setconductorId)
    setcontactNumber(res.data.contactNumber)
    })
    .catch((err)=>{
      console.log(err)
    })
          
    setconductorName(getData.conductorName)
    setconductorNIC(getData.conductorNIC)
    setconductorId(getData.setconductorId)
    setcontactNumber(getData.contactNumber)
    
          
    
  }, [])
    console.log(getData) 
   

    const submitData = (event) => {
        event.preventDefault();
        const values = {
            conductorName,
            conductorNIC,
            conductorId,
            contactNumber

        }
        axios.put(` http://localhost:8989/api/conductor/update_conductor/${id}`,values)
        .then((res)=>{
            console.log(res);
        })
        .catch((err)=>{
            console.log(err);
        })

    }
  return (
    <div className="form-container">
          <form className="addbus-form" onSubmit={submitData}>
              <h1>Edit Conductor details</h1>
            {submitted && valid ?<div className="alert alert-success ">Conductor insert Successfully!    <i class="fa fa-check"></i></div>:null}
            <input 
                onChange={(event)=>{setconductorName(event.target.value)}}
                value={values.conductorName}
                className="form-field"
                minlength="3"
                placeholder="Conductor Name"
                name="conductorName" />
            {submitted && !values.conductorName ? <span > <i class="fa fa-times-circle"></i>please enter a Conductor Name</span>:null}
            <input
                onChange={(event)=>{setconductorNIC(event.target.value)}} 
                value={values.conductorNIC}
                className="form-field"
                placeholder="NIC"
                maxlength="12"
                name="conductorNIC" />
            {submitted && !values.conductorNIC ? <span> <i class="fa fa-times-circle"></i>please enter a Conductor NIC</span>:null}
            <input 
                onChange={(event)=>{setconductorId(event.target.value)}}
                value={values.conductorId}
                className="form-field"
                placeholder="Conductor ID"
                minlength="4"
                maxlength="8"
                name="conductorId" />
                {submitted && !values.conductorId ?<span> <i class="fa fa-times-circle"></i>please enter a Conductor ID</span>:null}
            <input 
                onChange={(event)=>{setcontactNumber(event.target.value)}}
                value={values.contactNumber}
                className="form-field"
                placeholder="Contact Number"
                maxlength="10"
                name="Contact Number" />
                {submitted && !values.contactNumber ?<span> <i class="fa fa-times-circle"></i>please enter the Contact Number</span>:null}

            <div>
              <button class="btn btn-outline-warning btn-rounded waves-effect d-grid gap-2 col-6 mx-auto" type="submit">
                    Save
                  </button>
    
             </div> 
              
        </form>
    </div>
  )
}

export default UpdateConductor
