import React,{useState,useEffect} from 'react'
import './CategoryManagement.css';
import axios from 'axios';

function CategoryManagement() {


    const [categorys, setcategorys] = useState([]);
    const [state, setstate] = useState(initialState)
    
    useEffect(() => {
        axios.get(`http://localhost:8989/api/category/getallcategory`)
        .then(res => setcategorys(res.data) )
        .catch(error => console.log(error));
    },[]);

    axios.post(`http://localhost:8989/api/category/addcategory`,)



    
    
    const TigButton = (id,status)=>{
       
        console.log(`status = ${status}     id = ${id}`)
        
        var st = status;
        
        
        return(
            <input class="form-check-input cu-tig" type="checkbox" id="flexSwitchCheckDefault" checked={st} />
        )
    }
    


    return (
        <div className='ap-b-div'>
            <h1>Category Management</h1>

            <div className='cm-inner-div'>
                <form>
                    <div class="input-group mb-3">
                        <input type="text" className='form-control cm-input' placeholder="Enter your new Category here." aria-label="Recipient's username" aria-describedby="button-addon2"/>
                        <button className="btn btn-primary btn-custom" type="submit" id="button-addon2">ADD</button>
                    </div>
                </form>
            </div>
            <div className='table-div'>
                <table class="table table-secondary">
                    <thead>
                        <tr>
                        <th scope="col">#</th>
                        <th scope="col">Category</th>
                        <th scope="col">Status</th>
                        <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            categorys.map((category,index)=>(

                                <tr>
                                    <th scope="row">{index+1}</th>
                                        <td>{category.category}</td>
                                    <td>
                                        <div class="form-check form-switch">
                                            {TigButton(category._id,category.visible)}
                                            
                                        </div>

                                    </td>
                                    <td>
                                        <button type="button" class="btn btn-danger">Remove</button>
                                        
                                    </td>
                                </tr>
                            ))
                        }
                        
                    </tbody>
                </table>
            </div>
            
        </div>
    )
}

export default CategoryManagement
