import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Cards from '../Cards';
import './Drawing.css'

function Drawing() {
    const[url,setUrl] = useState("getg");

    const[category,setCategory] = useState("ALL");
    const[isTiggled, setTiggled] = useState(false);

    // function cardset(category) {
    //     return <Cards 
    //             status = {false}
    //             url = {`getg/${category}`}
    //             />;
    // }
    function categoryclick(category){
        console.log(category);
        setCategory(category);
        setTiggled(!isTiggled);
    }

    var categorys = [
        {
            category: "ALL"

        }
        ,
        {
            category: "Watercolor"

        },
        {
            category: "Pencil"

        },
        {
            category: "category 5"

        },
        {
            category: "category 6"

        },
        {
            category: "cat 1"

        },
        {
            category: "category 2"

        },
        {
            category: "category 3"

        },
        {
            category: "cat 4"

        },
        {
            category: "category 5"

        },
        {
            category: "category 6"

        }
        ,
        {
            category: "cat 1"

        },
        {
            category: "category 2"

        }
        
    ]
    
    return (
        
            <div >
                <div className='titlebar'>
                    <h1 className='subtitle'>Drawing</h1>
                </div>
                <div className='bordder-c'>
                    <div className='button-bar-div'>
                    {  
                        categorys.map((item, index)=>{
                            
                            return(
                                <div className='button-bar-inner' key={index}>
                                    <li href='#' className={`btn button-bar-btn `} onClick={()=>{
                                        setCategory(category);
                                        
                                    }}>{item.category}</li>
                                </div>
                            )
                        })
                        
                    }
                    </div>
                </div>
                
               {isTiggled && <Cards status = {false} url = {`getg/${category}`} />}
        
                
            </div>
            
            
    
    )
}

export default Drawing


