import React,{useEffect,useState,useContext} from 'react'
import axios from 'axios';
import './PostManagement.css'
import PostTable from './PostTable';
import {GetImgPreview} from '../user/GetImgPreview';
import CheckAdmin from './CheckAdmin';


function PostManagement() {
    const[products, setProducts] =useState([]);
    const {model,setmodel,tempImg,settempImg} = useContext(GetImgPreview);

    //************************************************ */
    let id = sessionStorage.getItem("AdminId")
    
    axios.post('http://localhost:8989/api/admin/checkadmin',{id:id})
    .then((res)=>{
        
        //console.log(res.data)
        if(res.data.success == true){
            console.log("hip hip hureee")
            return "true";
        }else{
            return "false";
        }
        
    })
    .catch((err)=>{
        console.log("this is error in post "+err)
        return "false";
    })
    //**************************************** *

    useEffect(() => {
        axios.get(`http://localhost:8989/product/getfortable/-1/title`)
        .then(res => setProducts(res.data.modelData))
        .catch(error => console.log(error));
    },[]);
    console.table(products)
    CheckAdmin().then(
        function(value){
            console.log(value)
        },
        function(error){
            console.log(error)
        }
    )
    //console.log(val)
    if(false){
        return (
                <div>
                    <div className={model? "model open" : "model"}>
                        <img src={tempImg}/>
                        <i class="fas fa-times" onClick={()=>{setmodel(false)}}></i>
                    </div>
                <div className='ap-b-div'>
                    <h1>Post Management</h1>
                    <PostTable products={products}/>

                </div>
                </div>
                
            )
    }else{
        return (
            <div></div>
        )
    }
    
}

export default PostManagement
