import axios from 'axios';
import React, { useState, useEffect, useContext} from 'react';
import {Link} from 'react-router-dom'
import {GetImgPreview} from '../user/GetImgPreview';
import {UserContext} from "../user/UserContext";


function CardItem(props) {
    const {User, setUser } = useContext(UserContext);
    const {model,setmodel,tempImg,settempImg} = useContext(GetImgPreview);
    //
    const [iconStatus,seticonStatus] = useState("far");
    const [isTuhmcsUp,setisTuhmcsUp] = useState(true);
    const [likeCount, setlikeCount] = useState(0);
    //const userID = User;
    function getCookie(cname) {
        let name = cname + "=";
        let decodedCookie = decodeURIComponent(document.cookie);
        let ca = decodedCookie.split(';');
        for(let i = 0; i <ca.length; i++) {
          let c = ca[i];
          while (c.charAt(0) == ' ') {
            c = c.substring(1);
          }
          if (c.indexOf(name) == 0) {
            console.log("cookes return!");
            return c.substring(name.length, c.length);
            
          }
        }
        return "";
    }
    const userID = getCookie("_id");

    useEffect(() => {
        axios.get(`http://localhost:8989/api/like/is_user_like/${userID}/${props.product._id}`)
        .then(res => setisTuhmcsUp(res.data.status))
        .catch(error => console.log(error));
    },[]);

    useEffect(() => {
        axios.get(`http://localhost:8989/api/like/get_like_count/${props.product._id}`)
        .then(res =>setlikeCount(res.data.count) )
        .catch(error => console.log(error));
    },[]);
    
    const thumbsClick = () =>{
        console.log("click!");
        
        if(userID == null){
            console.log("user id null");
            window.location.replace('/userlogin');
            
        }else{
            if(isTuhmcsUp){
                setisTuhmcsUp(false);
                
                axios.delete(`http://localhost:8989/api/like/like_delete/${userID}/${props.product._id}`)
                .then((res)=>{
                    console.log(res);
                    setlikeCount(likeCount-1);
                })
                .catch(()=>{
                    console.log("cannot delete!");
                })
            }else{
                setisTuhmcsUp(true);
                
                const newlike = {
                    userID:userID,
                    productID:props.product._id
                }
                axios.post('http://localhost:8989/api/like/add',newlike)
                .then((object)=>{
                    console.log(object);
                    setlikeCount(likeCount+1);
                }).catch((err)=>{
                    alert("sorry we can't add like! "+err)
                });
            }
           
        }

        

    }
    const showThumbs = () =>{
        if(isTuhmcsUp== true){
            seticonStatus("fas");
        }else{
            seticonStatus("far");
        }
    }
    
    useEffect(()=>{
        showThumbs();
        
    });
    function setimg(img){
        settempImg(img);
        setmodel(true);
        var plus = 1;
        const object = {
            "plus":plus
        }
        axios.put(`http://localhost:8989/product/view_update/${props.id}`,object)
        .then(()=>{
            
        }).catch((err)=>{
            alert("sorry we can't add like! "+err)
        });
    }

   
    return (
        <>
        
        <div className='pics'>
             <div className='outerBox'>
                <img src={props.src} alt='Travel' className='img-new'/>
                <div className='inner-box'>
                    <h4 className='cu_title'>{props.title}</h4>
                    <p className='cards__item__text'>{props.text}</p>
                    <div className='cu-idiv'>
                        <tr>
                            <th>
                                <i className={` ${iconStatus}  fa-heart cu-icon`} onClick={()=>{thumbsClick()}}></i>
                            </th>
                            <th>
                                <p className='cu-count'>{likeCount}</p>
                            </th>
                        </tr>
                        <tr>
                            <th>
                                <i className='fas fa-search-plus cu-icon-zoom' onClick={()=>{setimg(props.src)}} ></i>
                            </th>
                            <th>
                                <Link to="productdetails">
                                    <i class="fas fa-book-open cu-icon-zoom" onClick={setUser(props)}></i>
                                </Link>
                                
                            </th>
                        </tr>
                        
                    </div>
                </div>
             </div>
        </div>
        </>
    )
}

export default CardItem

