import React, { useState,useEffect,useContext } from 'react'
import { Link } from 'react-router-dom';
import './UserLogin.css';
import axios from 'axios';
import {UserContext} from "../UserContext";

function UserLogin() {

    const {User, setUser } = useContext(UserContext);
    const [email,setEmail] = useState("");
    const [password,setPassword] = useState("");
   // const [User, setUser] = useState([]);
   function setCookie(cname, cvalue, exdays) {
    const d = new Date();
    d.setTime(d.getTime() + (exdays*24*60*60*1000));
    let expires = "expires="+ d.toUTCString();
    document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
  }

    function Cheackuser(e){
        
        e.preventDefault();

        const newUser ={
            email,
            password
        }
        
        axios.post('http://localhost:8989/api/user/signin',newUser)
        .then((res)=>{
            console.log(res.data);
            if(res.data.success){
                setUser(res.data.id)
                console.log("log in sucsesful")
                setCookie("")
            }
        })
        .catch(error => console.log(error));
       
    };
    
    

    return (
        <div>
            <section>
                <div className='from-container'>
                    <h1>ligin from</h1>
                    <form onSubmit={Cheackuser}>
                        <div className='control'>
                            <lable for='email'>Email</lable>
                            <input type='email' name="email" id='email' onChange={(e)=>{
                                setEmail(e.target.value);
                            }}/>
                        </div>
                        <div className='control'>
                            <lable for='pasw'>Password</lable>
                            <input type='password' name="pasw" id='pasw' onChange={(e)=>{
                                setPassword(e.target.value);
                            }}/>
                        </div>
                        <div className='control'>
                            <input type='submit' value='Login'/>
                        </div>
                    </form>
                    <div className='link'>
                        <Link to='#'>
                            <a>Sing in</a>
                        </Link>
                    </div>
                </div>
            </section>
            
        </div>
    )
}

export default UserLogin
