import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CardItem from './CardItem';
import './Cards.css';


function Cards(props) {

    const[products, setProducts] =useState([]);
    var url = "";
    if(props.status == true){
        url = `get/${5}`;
    }else{
        url = `getg`
    }
    useEffect(() => {
        axios.get(`http://localhost:8989/product/${url}`)
        .then(res => setProducts(res.data.modelData))
        .catch(error => console.log(error));
    },[]);
    console.log(products.length/3);
    
    


    return (
        props.status == true?
        <div>
            <h3 className='main-header'>Top Five</h3>
            <div className='gallery'>
                {products.map((oneP,index)=>{
                    return(
                        <CardItem 
                            src={`http://localhost:8989/api/${oneP.productImage}`} 
                            text={`${oneP.description}`}
                            label='Adventure' 
                            path='/servise' 
                            title={`${oneP.title}`} 
                            likes={`${oneP.likes}`}
                            id = {`${oneP._id}`}
                        /> 
                    )
                })}
            </div>
        </div>
        
            :
            <div className='gallery'>
                {products.map((oneP,index)=>{
                    return(
                        <CardItem 
                            src={`http://localhost:8989/api/${oneP.productImage}`} 
                            text={`${oneP.description}`}
                            label='Adventure' 
                            path='/servise' 
                            title={`${oneP.title}`} 
                            likes={`${oneP.likes}`}
                            id = {`${oneP._id}`}
                        /> 
                    )
                })}
            </div>
            
    );
}

export default Cards;


