import axios from 'axios';
import React, { useState, useEffect,useContext } from 'react';
import {Link} from 'react-router-dom'
import { UserContext } from './UserContext';

function CardItem(props) {

    const {User, setUser } = useContext(UserContext);
    const [iconStatus,seticonStatus] = useState("far");
    const [isTuhmcsUp,setisTuhmcsUp] = useState(true);
    const [likeCount, setlikeCount] = useState(0);
    const userID = User;

    useEffect(() => {
        axios.get(`http://localhost:8989/api/like/is_user_like/${userID}/${props.id}`)
        .then(res => setisTuhmcsUp(res.data.status))
        .catch(error => console.log(error));
    },[]);

    useEffect(() => {
        axios.get(`http://localhost:8989/api/like/get_like_count/${props.id}`)
        .then(res =>setlikeCount(res.data.count) )
        .catch(error => console.log(error));
    },[]);
    
    const thumbsClick = () =>{
        console.log("click!");
        var plus = 0;
        if(userID == null){
            userID
        }else{
            if(isTuhmcsUp){
                setisTuhmcsUp(false);
                plus = -1;
                axios.delete(`http://localhost:8989/api/like/like_delete/${userID}/${props.id}`)
                .then((res)=>{
                    console.log(res);
                    setlikeCount(likeCount-1);
                })
                .catch(()=>{
                    console.log("cannot delete!");
                })
            }else{
                setisTuhmcsUp(true);
                plus = 1;
                const newlike = {
                    userID:userID,
                    productID:props.id
                }
                axios.post('http://localhost:8989/api/like/add',newlike)
                .then((object)=>{
                    console.log(object);
                    setlikeCount(likeCount+1);
                }).catch((err)=>{
                    alert("sorry we can't add like! "+err)
                });
            }
            const object = {
                "plus":plus
            }
            axios.put(`http://localhost:8989/product/like_update/${props.id}`,object)
            .then(()=>{
                
            }).catch((err)=>{
                alert("sorry we can't add like! "+err)
            });
        }

        

    }
    const showThumbs = () =>{
        if(isTuhmcsUp== true){
            seticonStatus("fas");
        }else{
            seticonStatus("far");
        }
    }
    
    useEffect(()=>{
        showThumbs();
        
    });
    return (
        <div className='pics'>
             <div className='outerBox'>
                <img src={props.src} alt='Travel' className='img-new'/>
                <div className='inner-box'>
                    <h4 className='cu_title'>{props.title}</h4>
                    <p className='cards__item__text'>{props.text}</p>
                    <div className='cu-idiv'>
                        <tr>
                            <th>
                                <i className={` ${iconStatus}  fa-heart cu-icon`} onClick={()=>{thumbsClick()}}></i>
                            </th>
                            <th>
                                <p className='cu-count'>{likeCount}</p>
                            </th>
                        </tr>
                    </div>
                </div>
             </div>
        </div>
    )
}

export default CardItem

