import axios from 'axios';
import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom'

function CardItem(props) {

    
    const [iconStatus,seticonStatus] = useState("far");
    const [isTuhmcsUp,setisTuhmcsUp] = useState(true);
    const [likeCount, setlikeCount] = useState(0);
    const userID = "61bf8efbf57e1d568ed3f4aa";

    useEffect(() => {
        axios.get(`http://localhost:8989/api/like/is_user_like/${userID}/${props.id}`)
        .then(res => setisTuhmcsUp(res.data.status))
        .catch(error => console.log(error));
    },[]);

    
    const thumbsClick = () =>{
        console.log("click!");
        var plus = 0;
        if(isTuhmcsUp){
            setisTuhmcsUp(false);
            plus = -1;
        }else{
            setisTuhmcsUp(true)
            plus = 1;
        }
        const object = {
            "plus":plus
        }
        // axios.put(`http://localhost:8989/product/like_update/${props.id}`,object)
        // .then(()=>{
        //     alert("like add!");
        // }).catch((err)=>{
        //     alert("sorry we can't add like! "+err)
        // });
        const newlike = {
            userID:userID,
            productID:props.id
        }
        axios.post('http://localhost:8989/api/like/add',newlike)
        .then((object)=>{
            console.log(object);
        }).catch((err)=>{
            alert("sorry we can't add like! "+err)
        });

    }
    const showThumbs = () =>{
        if(isTuhmcsUp== true){
            seticonStatus("fas");
        }else{
            seticonStatus("far");
        }
    }
    const CalLikeCount = () =>{
        useEffect(() => {
            axios.get(`http://localhost:8989/api/like/get_like_count/${userID}`)
            .then(res => console.log(res))
            .catch(error => console.log(error));
        },[]);
    }
    useEffect(()=>{
        showThumbs();
        CalLikeCount();
    });
    return (
        <>
            <li className='cards__item'>
                <Link className='cards__item__link' to={props.path}>
                    <figure className='cards__item__pic-wrap' data-category={props.lable}>
                        <img src={props.src} alt='Travel' className='cards__item__img'/>
                        
                    </figure>
                    <div className='cards__item__info'>
                        
                        
                        <th><i class={`${iconStatus} fa-thumbs-up`} onClick={()=>{thumbsClick()}}></i></th>
                        <th>{props.likes}</th>
                        
                        <h4>{props.title}</h4>
                        <h5 className='cards__item__text'>{props.text}</h5>
                        
                    </div>
                </Link>
            </li>
        </>
    )
}

export default CardItem
