import React,{useContext} from 'react'
import './PostTable.css'
import {GetImgPreview} from '../user/GetImgPreview';
import axios from 'axios';


function PostTable(probs) {
    console.table(probs.products)
    const products = probs.products;
    const {model,setmodel,tempImg,settempImg} = useContext(GetImgPreview);

    function setimg(img){
        settempImg(img);
        setmodel(true);
    }
    const GetLikeCount=(id)=>{
        axios.get(`http://localhost:8989/api/like/get_like_count/${id}`)
        .then((res)=>{
            console.log(res.data.count);
        } )
        .catch(error => console.log(error));
    }
    return (
        <div>
                            <table class="table table-secondary">
                    <thead>
                        <tr>
                        <th scope="col">#</th>
                        <th scope="col">image</th>
                        <th scope="col">Title</th>
                        <th scope="col">views</th>
                        <th scope="col">Likes</th>
                        <th scope="col">Category</th>
                        <th scope="col">Size</th>
                        <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            products.map((product,index)=>(

                                <tr>
                                    <th scope="row">{index+1}</th>
                                        <td>
                                            {
                                                <img className='table-img' src={`http://localhost:8989/api/${product.productImage}`} onClick={()=>{
                                                    setimg(`http://localhost:8989/api/${product.productImage}`)
                                                }}/>

                                            }
                                        </td>
                                        <td className='td-title'>
                                            {product.title}
                                        </td>
                                        <td>
                                            {product.likes}
                                            
                                        </td>
                                        <td>
                                            {GetLikeCount(product._id)}
                                            
                                        </td>
                                        <td>
                                            {product.category}
                                            
                                        </td>
                                        <td>
                                            {product.size}
                                            
                                        </td>
                                        <td>
                                            {}
                                            
                                        </td>
                                </tr>
                            ))
                        }
                        
                    </tbody>
                </table>
            
        </div>
    )
}

export default PostTable
