import React, { useEffect, useState } from "react"
import "payhere-embed-sdk/dist/react.css"
import Payhere from "payhere-embed-sdk/dist/react"
import './SubscriptionPage.css'
import axios from "axios"

const SubscriptionPage = () => {
  const [success, setSuccess] = useState(false)
  const [showPayhere, setShowPayhere] = useState(false)
  const [userData, setuserData] = useState(null)

  function getCookie(cname) {
    let name = cname + "=";
    let decodedCookie = decodeURIComponent(document.cookie);
    let ca = decodedCookie.split(';');
    for(let i = 0; i <ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) == ' ') {
        c = c.substring(1);
      }
      if (c.indexOf(name) == 0) {
        console.log("cookes return!")
        return c.substring(name.length, c.length)
        
      }
    }
    return "";
  }
  const userID = getCookie("_id");

  if(userID==null){
    console.log("user id null");
    window.location.replace('/userlogin');
  }

  useEffect(()=>{
    axios.get(`http://localhost:8989/api/user/getuser/${userID}`)
  .then((res)=>{
      setuserData(res)
  })
  },[])

  
  console.log(userData);

  return (
      <div id="payhere-modal">
        <div className="sp-main-containner">
          <div className='sp-from-container'>
            <h1>ligin from</h1>
            <form >
                <div className='sp-control'>
                    <lable for='email'>Email</lable>
                    <input type='email' name="email" id='email' onChange={(e)=>{
                        
                    }}/>
                </div>
                <div className='sp-control'>
                    <lable for='pasw'>Password</lable>
                    <input type='password' name="pasw" id='pasw' onChange={(e)=>{
                        
                    }}/>
                </div>
                <div className='sp-control'>
                  
                </div>
                
            </form>
          <div className="sp-containar">
              
          </div>        
          </div>
          
        

        <Payhere
          customerName="Johnathan Appleseed"
          customerEmail="john.appleseed@example.org"
          
          selector="#payhere-modal"
          embedURL={"https://app.payhere.co/altlabs/coffee"}
          open={showPayhere}
          onSuccess={data => {
            console.log("Payhere success", data)
            setSuccess(true)
          }}
          onFailure={err => {
            console.log("Payhere failed", err)
            setSuccess(true)
          }}
          onClose={() => {
            setShowPayhere(false)
            if (success) {
              console.log("Payment success")
            } else {
              console.log("Payment failed")
            }
          }}
        />
      </div>

    </div>

  )
}
export default SubscriptionPage
