import React,{useContext,useState} from 'react'
import './AdminLogin.css'
import { Link } from 'react-router-dom';
import axios from 'axios';
import {UserContext} from "../user/UserContext";

function AdminLogin() {
    const {User, setUser } = useContext(UserContext);
    const [userName,setuserName] = useState("");
    const [password,setPassword] = useState("");

    function Cheackuser(e){
        
        e.preventDefault();

        const newUser ={
            userName,
            password
        }
        
        axios.post('http://localhost:8989/api/admin/signin',newUser)
        .then((res)=>{
            console.log(res.data);
            if(res.data.success){
                setUser(res.data.id)
                console.log("log in sucsesful /n ")
               
            }
        })
        .catch(error => console.log(error));
       
    };

    return (
        <div>
        <section>
            <div className='from-container'>
                <h1>ligin from</h1>
                <from action='#'>
                    <div className='control'>
                        <lable for='name'>User Name</lable>
                        <input type='name' name="name" id='name' onChange={(e)=>{
                                setuserName(e.target.value);
                            }}/>
                    </div>
                    <div className='control'>
                        <lable for='pasw'>Password</lable>
                        <input type='password' name="pasw" id='pasw' onChange={(e)=>{
                                setPassword(e.target.value);
                            }}/>
                    </div>
                    <div className='control'>
                        <input type='submit' value='Login'/>
                    </div>
                </from>
                <div className='link'>
                   
                </div>
            </div>
        </section>
        
    </div>
    )
}

export default AdminLogin
